mapper.select:
select * 
from (select row_number() over(order by bq_no desc) as rown, board_qna.* from board_qna where bq_p_no=#{bq_p_no}) 
where rown between #{startNum} and #{endNum}

mapper.update:
update board_qna 
set bq_checkedSecret=#{bq_checkedSecret},
bq_title=#{bq_title}, bq_content=#{bq_content}, 
bq_writedate=default, 
bq_imagepath1=#{bq_imagepath1}, bq_imagepath2=#{bq_imagepath2},bq_imagepath3=#{bq_imagepath3}
where bq_no=#{bq_no};
